Use Postgres:

1. Compute the convex hull

SELECT ST_AsText(ST_ConvexHull(
	ST_Collect(ST_GeomFromText('MULTIPOINT(-118.2930253770832 34.02213045745779, -118.2913199203383 34.01841231888076, -118.2913501083079 34.02538924143019, 
			-118.2823107824859 34.01847795151199, -118.2803577256414 34.02187790063296,
			-118.2843176568676 34.02137409645614, 
			 -118.2877846117797 34.02230363506409, 
			-118.2895262860397 34.01980364229731, -118.283691805504 34.01990009483026, 
			-118.2853001011877 34.02307714181323)')
		)) );


2. Find out if the two polygons overlap

SELECT ST_Intersects(ST_GeomFromText('POLYGON((-118.2930253770832 34.02213045745779, -118.2913199203383 34.01841231888076, -118.2895262860397 34.01980364229731, -118.2836918055040 34.01990009483026, -118.2853001011877 34.02307714181323, -118.2930253770832 34.02213045745779))'),
   ST_GeomFromText('POLYGON((-118.2913501083079 34.02538924143019, -118.2823107824859 34.01847795151199, -118.2803577256414 34.02187790063296, -118.2843176568676 34.02137409645614, -118.2877846117797 34.02230363506409, -118.2913501083079 34.02538924143019))'));